import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

function pathView(fileName){
    return `../views/${fileName}.vue`;
}

function pathComponent(fileName){
    return `../components/${fileName}.vue`;
}

const routes = [
  {
    path: '/',
    name: 'LoginAdmin',
    component: () => import(pathView('Login'))
  },

  {
    path: '/admin',
    name: 'Admin',
    component: () => import(pathComponent('DashboardAdmin')),

    children: [
        {
            path: '/',
            name: 'HomeAdmin',
            component: () => import(pathView('HomeAdmin'))
        }
    ]
  }, 


  

]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
